//	</private>
//</requestedMonitorFilter>
//<monitorType>device</monitorType>
//</MonitorStart>

//The above XML text is stored in C:\Temp\MonitorDevice.xml file.

//The string that stores the error message.
string myError;

//Read the XML data into the sMonitorDeviceXMLDoc variable
StreamReader srXMLFile = new StreamReader("C:\\Temp\\MonitorDevice.xml");
char[] buffer = new char[srXMLFile.BaseStream.Length];
srXMLFile.Read(buffer, 0, buffer.Length );
string sMonitorDeviceXMLDoc = new string(buffer);

//Validate the XML text.
if (XMLValidateCSTADoc(sMonitorDeviceXMLDoc, out myError))
	Console.WriteLine("XML document text is Valid!");
else
	Console.WriteLine("XML document text is Invalid! Error message:\r\n{0}", myError);

